import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { asyncDelete, asyncLoadAll, setDetailingOptions } from '../redux/process/processAction';
import { loadProcessesFromStore, getHasDetailsProcess, getListingData, getShowDetailingEntry, getDetailingEntry } from '../redux/process/processSelector';

import ListingDetails from '../components/ListingDetails';
import ListingItem from '../components/ListingItem';
import Scaffold from '../components/Scaffold';
import '../styles/ListingView.css';
import { useHistory } from 'react-router';


import '../styles/NextListingView.css';

const ListingView = props => {
    const listData = useSelector(loadProcessesFromStore);
    const hasDetailsProcess = useSelector(getHasDetailsProcess)
    const dispatch = useDispatch();

    useEffect(() => {
    }, [hasDetailsProcess])

    useEffect(() => {
        dispatch(asyncLoadAll())
    }, [dispatch])

    return (
        <Scaffold>
            <div className={'listing-view__content'}>
                <div className={'listing-view__content--list'}>
                    {
                        listData &&
                        listData.length &&
                        listData.length > 0 &&
                        listData.map(process => (
                            <ListingItem
                                data={process}
                                key={process.id} />
                        ))
                    }
                </div>
                {
                    hasDetailsProcess &&
                    <div className={'listing-view__content--details'}>
                        <ListingDetails />
                    </div>
                }
            </div>
        </Scaffold>
    )
};


export const NextComponent = () => {
    const listData = useSelector(getListingData);
    const detailingEntry = useSelector(getDetailingEntry);
    const showDetailingEntry = useSelector(getShowDetailingEntry);
    const history = useHistory();
    const dispatch = useDispatch();

    const handleShowDetailing = (entry, flag) => {
        console.log(entry, flag);
        dispatch(setDetailingOptions(entry, flag));
    };

    useEffect(() => {
        dispatch(asyncLoadAll())
    }, [dispatch]);

    useEffect(() => {
        console.log(showDetailingEntry, detailingEntry);
    }, [showDetailingEntry, detailingEntry]);

    return (
        <div className={'listing-view__root'}>
            <div className={'listing-view__wrapper'}>
                <div className={'listing-view__header--wrapper'}>

                    <header className={'header'}>
                        <div className={'header__half'}>
                            <div className={'header__title--wrapper'}>
                                <h2 className={'header__title'}>Busca de processos</h2>
                            </div>
                            <div className={'header__search--wrapper'}>
                                <Search />
                            </div>
                        </div>
                        <div className={'header__half'}>
                            <div className={'header__button--wrapper'}>
                                <button
                                    className={'header__button white-button button gray-border shadow'}
                                    onClick={() => history.replace('/entry')}>
                                    NOVO
                                </button>
                            </div>
                        </div>
                    </header>

                </div>


                <div className={'listing-view__content--wrapper'}>

                    <article className={'content'}>
                        <section className={'content__section'}>
                            {
                                listData &&
                                listData.length &&
                                listData.length > 0 &&
                                <div className={'content__listing--wrapper'}>
                                    <Listing>
                                        {
                                            listData.map((item, index) => (
                                                <ListingItemm
                                                    data={item}
                                                    key={index}
                                                    showDetailing={handleShowDetailing} />
                                            ))
                                        }
                                    </Listing>
                                </div>
                            }
                        </section>
                        {
                            showDetailingEntry &&
                            <section className={'content__section'}>

                                <div className={'content__detailing--wrapper'}>
                                    <Detailing
                                        data={detailingEntry}
                                        showDetailing={handleShowDetailing} />
                                </div>

                            </section>
                        }
                    </article>

                </div>
            </div>
        </div>
    );
};

const Search = props => {


    return (
        <div className={'search shadow'}>
            <input
                className={'search__input'}
                placeholder={'Pesquise por uma informação do processo...'}
                type={'text'} />
            <button className={'search__button'}>
                <img src={'search_icon_32.svg'} />
            </button>
        </div>

    );
};

const Listing = props => {
    const { children } = props;

    return (
        <div className={'listing'}>
            {children}
        </div>
    );
};

const ListingItemm = props => {
    const { data, showDetailing } = props;

    return (
        <div
            className={'listing-item gray-border shadow'}
            onClick={() => showDetailing(data, true)}>
            <Cell heading={'Número'} content={data.processName} />
            <Cell heading={'Assunto'} content={data.processSubject} />
            <Cell heading={'Interessado'} content={data.processConcerns[0]} />
            <Cell heading={'Descrição'} content={data.processDescription} />
        </div>
    );
};

const Cell = props => {
    const { heading, content, sliceContent = true } = props;
    const transformString = (string, flag) => (
        flag &&
            string &&
            string.length &&
            string.length < 20 ?
            string :
            string.slice(0, 20)
    );

    useEffect(() => {
        console.log(heading, content);
    }, [heading, content])

    return (
        <div className={'cell'}>
            <div className={'cell__heading--wrapper'}>
                <h4 className={'cell__heading'}>{heading}</h4>
            </div>
            <div className={'cell__content--wrapper'}>
                <p className={'cell__content'}>{transformString(content, sliceContent)}</p>
                {props.children}
            </div>
        </div>
    );
};


const Detailing = props => {
    const { data, showDetailing } = props;
    const dispatch = useDispatch();

    const handleDelete = (entry, flag) => {
        dispatch(asyncDelete(entry.id));
        showDetailing(entry, flag);
    }
    console.log(data);

    return (
        <article className={'detailing shadow'}>
            <div className={'detailing__row'}>
                <Cell heading={'Processo'} content={data.processName} />
                <Cell heading={'Data'} content={data.processDate} />
                <div className={'detailing__close-wrapper'}>
                    <button
                        className={'detailing__close'}
                        onClick={() => showDetailing({}, false)}>X</button>
                </div>
            </div>

            <div className={'detailing__article--row'}>
                <Cell heading={'Assunto'} content={data.processSubject} />
            </div>

            <div className={'detailing__article--row'}>
                <Cell
                    heading={'Interessados'}
                    content={''}>
                    {
                        data.processConcerns &&
                        data.processConcerns.length > 0 &&
                        data.processConcerns.map((concerns, index) => (
                            <span
                                className={'cell__child'}
                                key={index}>
                                {concerns}
                            </span>
                        ))
                    }
                </Cell>
            </div>

            <div className={'detailing__article--row'}>
                <Cell heading={'Descrição'} content={data.processDescription} />
            </div>

            <div className={'detailing__article--footer'}>
                <button
                    className={'detailing__article--button button__label button-gray'}
                    onClick={() => handleDelete(data, false)}>
                    REMOVER
                        </button>
                <button className={'detailing__article--button button__label button-blue'}>EDITAR</button>
            </div>
        </article>
    );
};

export default ListingView;
