import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import Process from '../models/Process';
import '../styles/Register.css';
import { getName } from '../utils/ProcessNaming';
import ProcessService from '../services/ProcessService';

const Register = props => {
    const { id } = useParams();
    const [process, setProcess] = useState(EMPTY_PROCESS);
    const [processConcernment, setProcessConcernment] = useState('');

    useEffect(() => {
        console.log(process);
    }, [process]);

    useEffect(() => {
        // processConcernment == '' ?
        //     document.querySelector('#add__button').disabled :
        //     document.getElementById('#add__button').
    }, [processConcernment]);

    const emptyForm = () => {
        document.querySelector('form').reset();
        setProcess(EMPTY_PROCESS);
        console.log(process);
    }

    const setProcessProperty = (property, value) => {
        if (property == 'processConcernment') {
            setProcess({ ...process }, process.processConcernment.push(processConcernment));
            document.querySelector('#processConcernmentInput').value = '';
            setProcessConcernment('');
            return;
        }
        setProcess({ ...process, [property]: value });
    }

    return (
        <div className={'register__root'}>
            <div className={'register__form--wrapper'}>
                <h2 className={'title'}>Cadastro de processo</h2>
                <form
                    className={'register__form'}
                    onSubmit={event => {
                        event.preventDefault();
                        emptyForm();
                    }}>
                    <div className={'register__form--row'}>
                        <div className={'register__form--cell'}>
                            <label
                                className={'register__form--label'}
                                htmlFor={'processSubjectInput'}>
                                Assunto
                            </label>
                            <input
                                className={'register__form--input'}
                                id={'processSubjectInput'}
                                name={'processSubject'}
                                onChange={({ target }) => setProcessProperty(target.name, target.value)}
                                type={'text'} />
                        </div>
                    </div>
                    <div className={'register__form--row'}>
                        <div className={'register__form--cell'}>
                            <label
                                className={'register__form--label'}>
                                Interessados
                            </label>
                            {
                                process &&
                                process.processConcernment &&
                                process.processConcernment.length > 0 &&
                                process.processConcernment.map((processConcernment, index) =>
                                    <span key={index}>{processConcernment}</span>
                                )
                            }
                        </div>
                    </div>
                    <div className={'register__form--row'}>
                        <div className={'register__form--cell'}>
                            <label
                                className={'register__form--label'}
                                htmlFor={'processConcernmentInput'}>
                                Novo Interessado
                            </label>
                            <input
                                className={'register__form--input'}
                                id={'processConcernmentInput'}
                                name={'processConcernment'}
                                onChange={({ target }) => setProcessConcernment(target.value)}
                                type={'text'} />
                        </div>
                        <div className={'register__form--cell'}>
                            <button
                                className={'register__form--button button__label button'}
                                disabled={processConcernment.length > 0 ? false : true}
                                id={'add__button'}
                                onClick={event => {
                                    event.preventDefault();
                                    setProcessProperty('processConcernment', event.target.value)
                                }}>
                                ADICIONAR
                            </button>
                        </div>
                    </div>
                    <div className={'register__form--row'}>
                        <div className={'register__form--cell'}>
                            <label
                                className={'register__form--label'}
                                htmlFor={'processDescriptionInput'}>
                                Descrição
                            </label>
                            <textarea
                                className={'register__form--input full-width'}
                                id={'processDescriptionInput'}
                                name={'processDescription'}
                                onChange={({ target }) => setProcessProperty(target.name, target.value)}
                                type={'text'} />
                        </div>
                    </div>
                    <div className={'register__form--footer'}>
                        <button
                            className={'register__form--button button__label'}
                            type={'submit'}>
                            SALVAR
                        </button>
                    </div>
                </form>
            </div>
        </div >
    )
}

const EMPTY_PROCESS = {
    processName: '',
    processSubject: '',
    processDescription: '',
    processConcernment: [],
}
export default Register;
