import { processType } from '../type/processType';

export const handleSave = (process) => {
    return {
        type: processType.HANDLE_SAVE,
        payload: {
            process
        }
    }
}

export const handlePropertyChange = (property, value) => {
    return {
        type: processType.HANDLE_PROPERTY_CHANGE,
        payload: {
            property,
            value
        }
    }
}

export const handleConcernsChange = (property, value) => {
    return {
        type: processType.HANDLE_CONCERNS_CHANGE,
        payload: {
            property,
            value
        }
    }
}

export const handleConcernsAddition = (value) => {
    return {
        type: processType.HANDLE_CONCERNS_ADDITION,
        payload: {
            value
        }
    }
}

export const handleLoadSaga = () => {
    return {
        type: processType.HANDLE_LOAD_SAGA,
    }
}
