import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { asyncLoadAll } from '../redux/process/processAction';
import { loadProcessesFromStore, getHasDetailsProcess } from '../redux/process/processSelector';

import ListingDetails from '../components/ListingDetails';
import ListingItem from '../components/ListingItem';
import Scaffold from '../components/Scaffold';
import '../styles/ListingView.css';
import { useHistory } from 'react-router';


import '../styles/NextListingView.css';

const ListingView = props => {
    const listData = useSelector(loadProcessesFromStore);
    const hasDetailsProcess = useSelector(getHasDetailsProcess)
    const dispatch = useDispatch();

    useEffect(() => {
    }, [hasDetailsProcess])

    useEffect(() => {
        dispatch(asyncLoadAll())
    }, [dispatch])

    return (
        <Scaffold>
            <div className={'listing-view__content'}>
                <div className={'listing-view__content--list'}>
                    {
                        listData &&
                        listData.length &&
                        listData.length > 0 &&
                        listData.map(process => (
                            <ListingItem
                                data={process}
                                key={process.id} />
                        ))
                    }
                </div>
                {
                    hasDetailsProcess &&
                    <div className={'listing-view__content--details'}>
                        <ListingDetails />
                    </div>
                }
            </div>
        </Scaffold>
    )
};


export const NextComponent = () => {
    const history = useHistory();
    const dispatch = useDispatch();
    const listData = useSelector(loadProcessesFromStore);

    useEffect(() => {
        dispatch(asyncLoadAll())
    }, [dispatch]);

    return (
        <div className={'listing-view__root'}>
            <div className={'listing-view__wrapper'}>
                <div className={'listing-view__header--wrapper'}>

                    <header className={'header'}>
                        <div className={'header__half'}>
                            <div className={'header__title--wrapper'}>
                                <h2 className={'header__title'}>Busca de processos</h2>
                            </div>
                            <div className={'header__search--wrapper'}>
                                <Search />
                            </div>
                        </div>
                        <div className={'header__half'}>
                            <div className={'header__button--wrapper'}>
                                <button
                                    className={'header__button white-button button gray-border shadow'}
                                    onClick={() => history.replace('/entry')}>
                                    NOVO
                                </button>
                            </div>
                        </div>
                    </header>

                </div>


                <div className={'listing-view__content--wrapper'}>

                    <article className={'content'}>
                        <section className={'content__section'}>
                            {
                                listData &&
                                listData.length &&
                                listData.length > 0 &&
                                <div className={'content__listing--wrapper'}>
                                    <Listing>
                                        {
                                            listData.map((item, index) => (
                                                <ListingItemm
                                                    data={item}
                                                    key={index} />
                                            ))
                                        }
                                    </Listing>
                                </div>

                            }
                        </section>
                        <section className={'content__section'}>

                            <div className={'content__detailing--wrapper'}>
                                {/* //TODO Detailing component 'detailing' */}
                            </div>

                        </section>
                    </article>

                </div>
            </div>
        </div>
    );
};

const Search = props => {


    return (
        <div className={'search shadow'}>
            <input
                className={'search__input'}
                placeholder={'Pesquise por uma informação do processo...'}
                type={'text'} />
            <button className={'search__button'}>
                <img src={'search_icon_32.svg'} />
            </button>
        </div>

    );
};

const Listing = props => {
    const { children } = props;

    return (
        <div className={'listing'}>
            {children}
        </div>
    );
};

const ListingItemm = props => {
    const { data } = props;

    return (
        <div
            className={'listing-item gray-border shadow'}
            onClick={() => { }}>
            <Cell heading={'Número'} content={data.processName} />
            <Cell heading={'Assunto'} content={data.processSubject} />
            <Cell heading={'Interessado'} content={data.processConcerns[0]} />
            <Cell heading={'Descrição'} content={data.processDescription} />
        </div>
    );
};

const Cell = props => {
    const { heading, content } = props;

    return (
        <div className={'cell'}>
            <div className={'cell__heading--wrapper'}>
                <h4 className={'cell__heading'}>{heading}</h4>
            </div>
            <div className={'cell__content--wrapper'}>
                <p className={'cell__content'}>{content}</p>
            </div>
        </div>
    );
};

export default ListingView;
