import React from 'react';
import { useDispatch } from 'react-redux';
import { setDetailsProcess, setHasDetailsProcess } from '../redux/process/processAction';
import '../styles/ListingItem.css'

const ListingItem = props => {
    const { data } = props;
    const dispatch = useDispatch();

    const handleShowDetails = process => {
        dispatch(setDetailsProcess(process));
        dispatch(setHasDetailsProcess(true));
    }

    return (
        <div className={'listing-item__root'}>
            <div className={'listing-item__wrapper'}>
                <div
                    className={'listing-item__box'}
                    onClick={() => handleShowDetails(data)}>
                    <Cell title={'Número'} text={data.processName} />
                    <Cell title={'Assunto'} text={data.processSubject} />
                    <Cell title={'Interessado'} text={data.processConcerns[0]} />
                    <Cell title={'Descrição'} text={data.processDescription} />
                </div>
            </div>
        </div>
    )
}

const Cell = props => {
    const { title, text } = props;

    const resizeText = (text) => {
        if (text.length < 21) return text;
        return text.slice(0, 21);
    }

    return (
        <div className={'listing-item__box--cell'}>
            <h4 className={'listing-item__box--subtitle subtitle'}>{title}</h4>
            <p className={'listing-item__box--paragraph'}>{resizeText(text)}</p>
        </div>
    )
}

export default ListingItem;
