import ProcessService from '../../services/ProcessService';
import { processType } from './processType';
import { EMPTY_PROCESS, INITIAL_STATE } from '../../utils/constants';

export function processReducer(state = INITIAL_STATE, action) {
    const { type, payload } = action;
    switch (type) {
        case processType.HANDLE_SAVE:
            handleSave(ProcessService, state);
            break;
        case processType.HANDLE_PROPERTY_CHANGE:
            handlePropertyChange(state, payload);
            break;
        case processType.HANDLE_CONCERNS_CHANGE:
            handleConcernsChange(state, payload);
            break;
        case processType.HANDLE_CONCERNS_ADDITION:
            handleConcernsAddition(state);
            break;
        case processType.ASYNC_LOAD:
            return {
                ...state,
                processList: payload
            };
        case processType.ASYNC_INSERT:
            return {
                ...state,
                processEntry: EMPTY_PROCESS,
            };
        case processType.SET_DETAILING_OPTIONS:
            return {
                ...state,
                detailingEntry: payload.process,
                showDetailingEntry: payload.flag
            };
        case processType.SET_ENTRY_DIALOG_OPTIONS:
            return {
                ...state,
                showEntryDialog: payload.flag,
                processEntryId: payload.processEntryId,
            };
        case processType.PROCESS_ENTRY_PROPERTY_CHANGE:
            return {
                ...state,
                processEntry: {
                    ...state.processEntry,
                    [payload.property]: payload.value
                }
            };
        case processType.PROCESS_ENTRY_CONCERNS_ADDITION:
            return {
                ...state,
                processEntry: {
                    ...state.processEntry,
                    processConcerns: [
                        ...state.processEntry.processConcerns,
                        payload.value
                    ]
                }
            }
    }
    return state;
}

const handleConcernsAddition = (state) => {
    state.process.processConcerns
        .push(state.newProcessConcerned);
}

const handleConcernsChange = (state, payload) => {
    state.newProcessConcerned = payload.value;
}

const handlePropertyChange = (state, payload) => {
    state.process = {
        ...state.process,
        [payload.property]: payload.value
    };
}

const handleSave = (processService, state) => {
    processService
        .createProcess(state.process)
        .then(response => console.log(response))
        .catch(err => console.error(err));
}
