import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router';
import { setDetailsProcess, setHasDetailsProcess, asyncDelete, asyncLoadAll } from '../redux/process/processAction';
import { getDetailsProcess } from '../redux/process/processSelector';
import '../styles/ListingDetails.css';

const ListingDetails = () => {
    const detailsProcess = useSelector(getDetailsProcess);
    const dispatch = useDispatch();
    const history = useHistory();

    const handleShowDetails = flag => {
        dispatch(setDetailsProcess({}));
        dispatch(setHasDetailsProcess(flag));
    };

    const handleDelete = processId => {
        dispatch(asyncDelete(processId));
        dispatch(asyncLoadAll());
        handleShowDetails(false);
        history.replace('/listing');
    }

    useEffect(() => {
        console.log(detailsProcess);
    }, [detailsProcess]);

    return (
        <div className={'listing-details__root'}>
            <div className={'listing-details__wrapper'}>
                <article className={'listing-details__article'}>
                    <div className={'listing-details__article--row'}>
                        <div className={'listing-details__article--cell'}>
                            <h4 className={'listing-details__article--subtitle subtitle'}>Processo</h4>
                            <span className={'listing-details__article--info'}>{detailsProcess.processName}</span>
                        </div>
                        <div className={'listing-details__article--cell'}>
                            <h4 className={'listing-details__article--subtitle subtitle'}>Data</h4>
                            <span className={'listing-details__article--info'}>{detailsProcess.processDate}</span>
                        </div>
                        <div className={'listing-details__article--close'}>
                            <button
                                className={'listing-details__article--close-button'}
                                onClick={() => handleShowDetails(false)}>X</button>
                        </div>
                    </div>

                    <div className={'listing-details__article--row'}>
                        <div className={'listing-details__article--cell'}>
                            <h4 className={'listing-details__article--subtitle subtitle'}>Assunto</h4>
                            <span className={'listing-details__article--info'}>{detailsProcess.processSubject}</span>
                        </div>
                    </div>

                    <div className={'listing-details__article--row'}>
                        <div className={'listing-details__article--cell'}>
                            <h4 className={'listing-details__article--subtitle subtitle'}>Interessados</h4>
                            <div className={'listing-details__article--info'}>
                                {
                                    detailsProcess.processConcerns &&
                                    detailsProcess.processConcerns.length > 0 &&
                                    detailsProcess.processConcerns.map((concerns, index) => (
                                        <span
                                            className={'listing-details__article--info--concerns'}
                                            key={index}>
                                            {concerns}
                                        </span>
                                    ))
                                }
                            </div>
                        </div>
                    </div>

                    <div className={'listing-details__article--row'}>
                        <div className={'listing-details__article--cell'}>
                            <h4 className={'listing-details__article--subtitle subtitle'}>Descrição</h4>
                            <p className={'listing-details__article--paragraph'}>{detailsProcess.processDescription}</p>
                        </div>
                    </div>

                    <div className={'listing-details__article--footer'}>
                        <button
                            className={'listing-details__article--button button__label button-gray'}
                            onClick={() => handleDelete(detailsProcess.processId)}>
                            REMOVER
                        </button>
                        <button className={'listing-details__article--button button__label button-blue'}>EDITAR</button>
                    </div>
                </article>
            </div>
        </div>
    )
};

export default ListingDetails;
