import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { handleLoadSaga } from '../redux/process/processAction';
import { loadProcessesFromStore } from '../redux/process/processSelector';

import ListingItem from '../components/ListingItem';
import Scaffold from '../components/Scaffold';
import '../styles/ListingView.css';

const ListingView = () => {
    const listData = useSelector(loadProcessesFromStore);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(handleLoadSaga())
    }, [dispatch, listData])

    return (
        <Scaffold>
            {
                listData &&
                listData.length &&
                listData.length > 0 &&
                listData.map(process => (
                    <ListingItem
                        data={process}
                        key={process.id} />
                ))
            }
        </Scaffold>
    )
};

export default ListingView;
