import { processType } from './processType';

export const handlePropertyChange = (property, value) => {
    return {
        type: processType.HANDLE_PROPERTY_CHANGE,
        payload: {
            property,
            value
        }
    }
}

export const handleConcernsChange = (property, value) => {
    return {
        type: processType.HANDLE_CONCERNS_CHANGE,
        payload: {
            property,
            value
        }
    }
}

export const handleConcernsAddition = (value) => {
    return {
        type: processType.HANDLE_CONCERNS_ADDITION,
        payload: {
            value
        }
    }
}

export const asyncLoadAll = () => {
    return {
        type: processType.ASYNC_LOAD_ALL,
    }
}

export const asyncInsert = (process) => {
    return {
        type: processType.ASYNC_INSERT,
        payload: {
            process
        }
    }
}

export const asyncUpdate = process => {
    return {
        type: processType.ASYNC_UPDATE,
        payload: {
            process
        }
    }
}

export const asyncDelete = processId => {
    return {
        type: processType.ASYNC_DELETE,
        payload: {
            processId
        }
    }
}

export const setDetailingOptions = (process, flag) => {
    return {
        type: processType.SET_DETAILING_OPTIONS,
        payload: {
            process,
            flag
        }
    }
}


//
export const setEntryDialogOptions = (flag, process) => {
    return {
        type: processType.SET_ENTRY_DIALOG_OPTIONS,
        payload: {
            flag,
            processEntryId,
        }
    }
}

export const asyncLoadOne = processId => {
    return {
        type: processType.ASYNC_LOAD_ONE,
        payload: {
            processId
        }
    }
}
