import React, { useEffect } from 'react';
import { connect, useDispatch, useSelector } from 'react-redux';
import { handleLoadSaga } from '../redux/process/processAction';

import ListingItem from '../components/ListingItem';
import Scaffold from '../components/Scaffold';
import '../styles/Listing.css';
import { loadProcessesFromStore } from '../redux/process/processSelector';

const ListingView = ({ data }) => {
    const listData = useSelector(loadProcessesFromStore);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(handleLoadSaga())
    }, [dispatch])

    return (
        <Scaffold>
            {
                listData &&
                listData.length &&
                listData.length > 0 &&
                listData.map(process => {
                    <ListingItem
                        data={process}
                        key={process.id} />
                })
            }
        </Scaffold>
    )
};

export default connect(state => ({ data: state.processList }))(ListingView);
