import ProcessService from '../../services/ProcessService';
import { processType } from '../type/processType';

const EMPTY_PROCESS = {
    processName: '',
    processSubject: '',
    processDescription: '',
    processConcerns: [],
}

const INITIAL_STATE = {
    process: EMPTY_PROCESS,
    processList: []
}

export function processReducer(state = INITIAL_STATE, action) {
    console.log('processRed', state, action);
    const { type, payload } = action;
    switch (type) {
        case processType.HANDLE_SAVE:
            ProcessService
                .createProcess(payload.process)
                .then(response => console.log(response))
                .catch(err => console.error(err));
            break;
        case processType.HANDLE_PROPERTY_CHANGE:
            payload.property == 'processConcerns' ?
                setProcessProperty(state, payload, true) :
                setProcessProperty(state, payload);

        // state.process = {
        //     ...state.process,

        // }

        // if (property == 'processConcerns') {
        //     setProcess({ ...process }, process.processConcerns.push(processConcerns));
        //     document.querySelector('#processConcernsInput').value = '';
        //     setProcessConcernment('');
        //     return;
        // }
        // setProcess({ ...process, [property]: value });
    }
    return state;
}

const setProcessProperty = (state, payload, flag = false) => {
    state.process = {
        ...state.process,
        [payload.property]: flag ?
            payload.property.push(payload.value) :
            payload.value
    }
}
