import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router';
import { asyncDelete, asyncLoadAll, setDetailingOptions } from '../redux/process/processAction';
import { getListingData, getShowDetailingEntry, getDetailingEntry } from '../redux/process/processSelector';
import '../styles/ListingView.css';

import Search from '../components/Search';

const ListingView = () => {
    const listData = useSelector(getListingData);
    const detailingEntry = useSelector(getDetailingEntry);
    const showDetailingEntry = useSelector(getShowDetailingEntry);
    const history = useHistory();
    const dispatch = useDispatch();

    const handleShowDetailing = (entry, flag) => {
        dispatch(setDetailingOptions(entry, flag));
    };

    useEffect(() => {
        dispatch(asyncLoadAll());
    }, [dispatch]);

    return (
        <div className={'listing-view__root'}>
            <div className={'listing-view__wrapper'}>
                <div className={'listing-view__header--wrapper'}>
                    <header className={'header'}>
                        <div className={'header__half'}>
                            <div className={'header__title--wrapper'}>
                                <h2 className={'header__title'}>Busca de processos</h2>
                            </div>
                            <div className={'header__search--wrapper'}>
                                <Search />
                            </div>
                        </div>
                        <div className={'header__half'}>
                            <div className={'header__button--wrapper'}>
                                <button
                                    className={'header__button white-button button gray-border shadow'}
                                    onClick={() => history.replace('/entry')}>
                                    NOVO
                                </button>
                            </div>
                        </div>
                    </header>
                </div>

                <div className={'listing-view__content--wrapper'}>
                    <article className={'content'}>
                        <section className={'content__section'}>
                            {
                                listData &&
                                listData.length &&
                                listData.length > 0 &&
                                <div className={'content__listing--wrapper'}>
                                    <Listing>
                                        {
                                            listData.map((item, index) => (
                                                <ListingItem
                                                    data={item}
                                                    key={index}
                                                    showDetailing={handleShowDetailing} />
                                            ))
                                        }
                                    </Listing>
                                </div>
                            }
                        </section>
                        {
                            showDetailingEntry &&
                            <section className={'content__section'}>

                                <div className={'content__detailing--wrapper'}>
                                    <Detailing
                                        data={detailingEntry}
                                        showDetailing={handleShowDetailing} />
                                </div>

                            </section>
                        }
                    </article>
                </div>
            </div>
        </div>
    );
};


const Listing = props => {
    const { children } = props;

    return (
        <div className={'listing'}>
            {children}
        </div>
    );
};

const ListingItem = props => {
    const { data, showDetailing } = props;

    return (
        <div
            className={'listing-item gray-border shadow'}
            onClick={() => showDetailing(data, true)}>
            <Cell heading={'Número'} content={data.processName} />
            <Cell heading={'Assunto'} content={data.processSubject} />
            <Cell heading={'Interessado'} content={data.processConcerns[0]} />
            <Cell heading={'Descrição'} content={data.processDescription} />
        </div>
    );
};

const Cell = props => {
    const { heading, content, sliceContent = true } = props;
    const transformString = (string, flag) => {
        if (!flag) return string;
        return string &&
            string.length &&
            string.length < 20 ?
            string :
            string.slice(0, 20)
    };

    return (
        <div className={'cell'}>
            <div className={'cell__heading--wrapper'}>
                <h4 className={'cell__heading'}>{heading}</h4>
            </div>
            <div className={'cell__content--wrapper'}>
                {
                    !props.children &&
                    <p className={'cell__content'}>{transformString(content, sliceContent)}</p>
                }
                {props.children}
            </div>
        </div>
    );
};


const Detailing = props => {
    const { data, showDetailing } = props;
    const dispatch = useDispatch();

    const handleDelete = (entry, flag) => {
        showDetailing({}, flag);
        dispatch(asyncDelete(entry.id));
        dispatch(asyncLoadAll());
    };

    return (
        <article className={'detailing shadow'}>
            <div className={'detailing__row'}>
                <Cell heading={'Processo'} content={data.processName} />
                <Cell heading={'Data'} content={data.processDate} />
                <div className={'detailing__close-wrapper'}>
                    <button
                        className={'detailing__close'}
                        onClick={() => showDetailing({}, false)}>X</button>
                </div>
            </div>

            <div className={'detailing__row'}>
                <Cell heading={'Assunto'} content={data.processSubject} />
            </div>

            <div className={'detailing__row'}>
                <Cell
                    heading={'Interessados'}
                    content={''}>
                    {
                        data.processConcerns &&
                        data.processConcerns.length > 0 &&
                        data.processConcerns.map((concerns, index) => (
                            <span
                                className={'cell__child'}
                                key={index}>
                                {concerns}
                            </span>
                        ))
                    }
                </Cell>
            </div>

            <div className={'detailing__row'}>
                <Cell heading={'Descrição'} content={data.processDescription} sliceContent={false} />
            </div>

            <div className={'detailing__footer'}>
                <button
                    className={'detailing__button button white-button button__label shadow gray-border'}
                    onClick={() => handleDelete(data, false)}>
                    REMOVER
                        </button>
                <button className={'detailing__button button white-button button__label shadow blue-border'}>
                    EDITAR
                </button>
            </div>
        </article>
    );
};

export default ListingView;
